INSERT INTO public.edition (id, isbn, binding, pages, price, description, release_date, book_id, pub_id) VALUES (2, '9780134685991', 'твердый', 464, 22000, 'Третье издание охватывает особенности языка программирования и библиотек, появившихся в Java 7, 8 и 9, в том числе конструкции функционального программирования, добавленные к своим объектно-ориентированным корням. В книгу включены также многие новые советы, а также глава, посвященная лямбда-выражениям и потокам.', '2018-01-03', 4, 2);